var vm = new Vue({
  el: "#rs-wrapper",
  data: {
    signup: true
  },
  mounted: function() {
    this.initPlugins();
  },
  methods: {
    initPlugins: function() {
      var vm = this,
          inputPass = $(vm.$el).find('input.pass-strength'),
          formValidate = $(vm.$el).find('form.form-validator'),
          emailInputs = $(vm.$el).find('input[type="email"]');
      inputPass.strengthMeter('text', {
        container: $('.help-block'),
        hierarchy: {
          '0': ['text-danger help-block text-right pass-strength-text', ''],
          '1': ['text-danger help-block text-right pass-strength-text', 'Think about a stronger password!'],
          '5': ['text-warning help-block text-right pass-strength-text', 'Well, you are doing better ...'],
          '10': ['text-warning help-block text-right pass-strength-text', 'Keep going ...'],
          '15': ['text-success help-block text-right pass-strength-text', 'Ok, this will do!']
        }
      });
      formValidate.validator();

      emailInputs.emailautocomplete({
        suggClass: "custom-sugg"
      });
    },
    switchPage: function() {
      if(!this.signup) {
        this.signup = true;
        setTimeout(function() {
          vm.initPlugins();
        },500);
      } else {
        this.signup = false;
        setTimeout(function() {
          vm.initPlugins();
        },500);
      }
    },
    checkInput: function() {
      var input = $(event.target);
      if(!input.val().length){
        input.parent().removeClass('has-error has-danger')
      } else {
        return false;
      }
    }
  }
})

$(document).ready(function() {
  $('.full-height-content.vertical-center').removeClass('overflow');
});
