Vue.component('color-picker', {
  template: '#color-picker',
  props: ['type', 'value'],
  data: function() {
    return {
      internalValue: ''
    }
  },
  watch: {
    internalValue: function() {
      this.$emit('input', this.internalValue);
    }
  },
  created: function() {
    this.internalValue = this.value
  },
  mounted: function() {
    var vm = this,
        elem = $(this.$el).children('input');
    if(elem.hasClass('basic')) {
      elem.val(this.internalValue).spectrum({
        showInput: true,
        showAlpha: true,
        preferredFormat: "hex",
        change: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        move: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        dragstart: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        dragstop: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        }
      });
    } else if(elem.hasClass('in-root')) {
      elem.val(this.internalValue).spectrum({
        showInput: true,
        showAlpha: true,
        preferredFormat: "hex",
        appendTo: 'body',
        change: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        move: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        dragstart: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        dragstop: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        }
      });
    } else {
      elem.val(this.internalValue).spectrum({
        allowEmpty:true,
        showInput: true,
        showAlpha: true,
        preferredFormat: "hex",
        change: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        move: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        dragstart: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        },
        dragstop: function(color) {
          this.internalValue = color.toRgbString();
          vm.$emit('input', this.internalValue);
        }
      });
    }
  }
});

Vue.component('selectize', {
  template: '#selectize',
  props: ['custom', 'value', 'options', 'placeholder'],
  data: function() {
    return {
      internalValue: ''
    }
  },
  watch: {
    internalValue: function() {
      this.$emit('input', this.internalValue);
    }
  },
  created: function() {
    this.internalValue = this.value
  },
  mounted: function() {
    var vm = this;
    if($(vm.$el).hasClass('selectize-iconbar')) {
      $(vm.$el).selectize({
        labelField: 'label',
        valueField: 'value',
        searchField: 'label',
        render:       {
              option: function(item, escape) {
                return '<div>'
                  + '<span class="'+item.value+'"></span> '
                  + '</div>';
              },
              item: function(data) {
          return '<div>' + '<span class="'+data.value+'"> ' + ' </div>';
          }
        },
        onDropdownOpen: function($dropdown) {
          $dropdown.find('.selectize-dropdown-content').niceScroll({
            cursorborderradius: "0px",
            cursorborder : false,
            cursorcolor:"#000000",
            cursoropacitymax: 0.30,
            horizrailenabled: false,
          }).resize();
        },
        onChange: function(value) {
          vm.internalValue = value;
          vm.$emit('input', vm.internalValue);
        }
      });
    } else {
      $(vm.$el).selectize({
        // create: true,
        onDropdownOpen: function($dropdown) {
          $dropdown.find('.selectize-dropdown-content').niceScroll({
            cursorborderradius: "0px",
            cursorborder : false,
            cursorcolor:"#000000",
            cursoropacitymax: 0.30,
            horizrailenabled: false,
          }).resize();
        },
        onChange: function(value) {
          vm.internalValue = value;
          vm.$emit('input', vm.internalValue);
        }
      });
    }
  }
});

Vue.directive('slideToggle', function(el, binding){
  setTimeout(function(){
    if(binding.value){
      $(el).slideDown(200);
    } else {
      $(el).slideUp(200);
    }
  }, 0)
});

